function getChapterNum(it) {
    if (it == null || it.text == null) {
        return -1;
    }
    it = it.text || "";
    if (it == "") {
        return -1;
    }
    it = clearText(it);
    let reg = /^[0-9]*$/;
    if (!reg.test(it)) {
        return -1;
    }
    it = parseInt(it);
    if (isNaN(it)) {
        return -1;
    }
    return it;
}

