            //html: it,
            text: t2 && t2.includes(key) ? t2 : t,
            href: pd(it, "a&&href", url)
        }
    });

    function isResult(it) {
        it = it.text || "";
        if (it == "" || it.match(/搜索|结果/)) {
            return false
        }
        return it.includes(key);
    }

    for (let i = 0; i < arr.length; i++) {
        let it = arr[i];
        let t = it.text;
