            body: "url=" + config.url + "&vkey=" + config.vkey + "&token=" + config.token + "&sign=smdyycc",
            method: "POST"
        });
        var json = JSON.parse(apiHtml);
        if (json.code == 200) {
            require("https://disdk.oss-cn-shenzhen.aliyuncs.com/play/js/decode.js");
