var d = [];
var html = getResCode();

var lazy = $('').lazyRule(() => {
    function btoa(str) {
        return new Buffer.from(str).toString("base64");
    };

    function atob(b64) {
        eval(getCryptoJS());
        return CryptoJS.enc.Base64.parse(b64).toString(CryptoJS.enc.Latin1)
    };

    function rc4(data, key, t) {
        var pwd = key || 'ffsirllq';
        var cipher = '';
        var key = [];
        var box = [];
        var pwd_length = pwd.length;
        if (t == 1) {
            var data = atob(data);
        } else {
            var data = encodeURIComponent(data);
        }

        var data_length = data.length;

        for (i = 0; i < 256; i++) {
            key[i] = pwd[i % pwd_length].charCodeAt();
            box[i] = i;
        }
        for (j = i = 0; i < 256; i++) {
            j = (j + box[i] + key[i]) % 256;
            tmp = box[i];
            box[i] = box[j];
            box[j] = tmp;
        }
        for (a = j = i = 0; i < data_length; i++) {
            a = (a + 1) % 256;
            j = (j + box[a]) % 256;
            tmp = box[a];
            box[a] = box[j];
            box[j] = tmp;
            k = box[((box[a] + box[j]) % 256)];
            cipher += String.fromCharCode(data[i].charCodeAt() ^ k);
        }
        if (t == 1) {
            return decodeURIComponent(cipher);
        } else {
            return btoa(cipher);
        }
    };

    function toUrl(playUrl) {
        if (/mgtv|sohu/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0 (Windows NT 10.0)}";
        } else if (/bili/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0&&Referer@https://www.bilibili.com}";
        } else if (/ixigua/.test(playUrl)) {
            return playUrl + "#isVideo=true#" + "#.mp4;{Referer@https://www.ixigua.com/&&User-Agent@Mozilla/5.0}";
        } else {
            return playUrl;
        }
    }

    var playUrl = "";
    try {
        var html = request(input, {});
        var player_data = JSON.parse(html.match(/r player_.*?=(.*?)</)[1]);
        var fro = player_data.from;
        playUrl = player_data.url;

        if (player_data.encrypt == '1') {
            playUrl = unescape(player_data.url);
        } else if (player_data.encrypt == '2') {
            playUrl = unescape(base64Decode(player_data.url));
        };
        var exclude = /playm3u8|m3u8\.tv|min\.css|404\.m3u8|\.css/;
        var contain = /\.mp4|\.m3u8|\.flv|\.avi|\.mpeg|\.wmv|\.mov|\.rmvb|\.dat|qqBFdownload|mime=video%2F|video_mp4/;
        if (!exclude.test(playUrl) && contain.test(playUrl)) {
            return toUrl(playUrl);
        }
        if (fro == 'ffm3u8' || fro == 'BYGA' || fro == 'lg' || fro == 'lg1') {
            var jxUrl = 'https://jx1.bw66.xyz/m3u8/?url=' + playUrl;
            var reqHtml = fetch(jxUrl);
            eval(reqHtml.match(/var config = {[\s\S]*?}/)[0] + "");
            var apiUrl = 'https://jx1.bw66.xyz/m3u8/API.php';
            var apiHtml = request(apiUrl, {
                headers: {
                    "User-Agent": "Mozilla/5.0 (Windows NT 10.0)"
                },
                body: "url=" + config.url + "&time=" + config.time + "&key=" + config.key,
                method: "POST"
            });
            var json = JSON.parse(apiHtml);
            if (json.code == 200) {
                playUrl = json.url;
                return toUrl(playUrl);
            } else {
                return 'toast://' + json.msg;
            }

        } else {
            var jxUrl = 'https://jx1.bw66.xyz/player/analysis.php?v=' + playUrl;
            var jxHtml = request(jxUrl, {
                headers: {
                    "User-Agent": "Mozilla/5.0 (Windows NT 10.0)",
                    "Referer": "https://jx1.bw66.xyz/player/?url=" + playUrl
                },
                method: "GET"
            });
            eval(jxHtml.match(/var config = {[\s\S]*?}/)[0] + "");
            playUrl = rc4(config.url, '202205051426239465', 1);
            //log(playUrl)
            return toUrl(playUrl);
        }

    } catch (e) {
         //setError(e);
        showLoading("正在进行检索，请稍候...");
        var video = 'x5Rule://' + input + '@' + $.toString(() => {
            //fba.log(fba.getUrls())
            var urls = _getUrls()
            var exclude = /playm3u8|m3u8\.tv|min\.css|404\.m3u8|\.css/;
            var contain = /\.mp4|\.m3u8|\.flv|\.avi|\.mpeg|\.wmv|\.mov|\.rmvb|\.dat|qqBFdownload|mime=video%2F|video_mp4/;
            for (var i in urls) {
                if (!exclude.test(urls[i]) && contain.test(urls[i])) {
                    // fy_bridge_app.log(urls[i])
                    if (/mgtv|sohu/.test(urls[i])) {
                        return urls[i] + ";{User-Agent@Mozilla/5.0 (Windows NT 10.0)}";
