    var html2 = playerHtml.replace(dStr, 'ä½ å¤§çˆ·').replace(kStr, 'ä½ å¤§å¦ˆ');
    //log(html2);
    var mydata = html2.match(/ä½ å¤§çˆ·="(.*?)"/)[1];
    var myKey = html2.match(/var ä½ å¤§å¦ˆ\("(.*?)"/)[1];
	var myIv = html2.match(/var iv=md5.enc.Utf8.parse\((.*?)\)/)[1];
    //log(mydata);
